import React, { useContext, useMemo } from 'react';
import { Box, Grid, Typography, Paper, Fade } from '@mui/material';
import { useTheme } from '@mui/material/styles';
import { Radar } from 'react-chartjs-2';
import { DataContext } from '../context/DataContext';
import SummaryStats from '../components/charts/SummaryStats';

// Import and register Chart.js components for Radar chart
import {
  Chart,
  RadarController,
  RadialLinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend,
} from 'chart.js';

// Register the components
Chart.register(
  RadarController,
  RadialLinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend
);

const ethiopianMapUrl = 'https://upload.wikimedia.org/wikipedia/commons/thumb/7/71/Flag_of_Ethiopia.svg/1200px-Flag_of_Ethiopia.svg.png';
const educationIconUrl = 'https://png.pngtree.com/png-vector/20230417/ourmid/pngtree-3d-graduation-cap-icon-vector-png-image_6704102.png';

const Summary = () => {
  const { data, dataType } = useContext(DataContext);
  const theme = useTheme();
  const chartRefs = {
    radarChartRef: React.createRef(),
  };

  const filteredData = useMemo(() => {
    return data.filter((student) => student.sex === 'Male' || student.sex === 'Female');
  }, [data]);

  const getRadarChartData = () => {
    if (!filteredData || filteredData.length === 0) {
      return {
        labels: ['No Data'],
        datasets: [{
          label: 'Summary Metrics',
          data: [0, 0],
          backgroundColor: 'rgba(204, 204, 204, 0.2)',
          borderColor: '#ccc',
          borderWidth: 2,
        }],
      };
    }

    const overallData = filteredData.reduce(
      (acc, student) => {
        acc.totalScore += Number(dataType === 'HighSchool' ? student.average : student.score) || 0;
        acc.totalPass += dataType === 'HighSchool' ? (student.average >= 50 ? 1 : 0) : (['excellent', 'good'].includes(student.status) ? 1 : 0);
        return acc;
      },
      { totalScore: 0, totalPass: 0 }
    );
    const avgScore = filteredData.length > 0 ? (overallData.totalScore / filteredData.length).toFixed(2) : 0;
    const successRate = filteredData.length > 0 ? ((overallData.totalPass / filteredData.length) * 100).toFixed(2) : 0;

    return {
      labels: ['Average Score', 'Success Rate (%)'],
      datasets: [{
        label: 'Overall Summary',
        data: [avgScore, successRate],
        backgroundColor: 'rgba(0, 122, 255, 0.2)', // Apple blue with transparency
        borderColor: '#007AFF', // Apple blue
        borderWidth: 2,
        pointBackgroundColor: '#007AFF',
        pointBorderColor: '#fff',
        pointHoverBackgroundColor: '#fff',
        pointHoverBorderColor: '#007AFF',
      }],
    };
  };

  const downloadCSV = () => {
    const csvContent = 'data:text/csv;charset=utf-8,' + filteredData.map((row) => Object.values(row).join(',')).join('\n');
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement('a');
    link.setAttribute('href', encodedUri);
    link.setAttribute('download', `${dataType}_summary_data.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const downloadJSON = () => {
    const jsonContent = JSON.stringify(filteredData, null, 2);
    const blob = new Blob([jsonContent], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `${dataType}_summary_data.json`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const downloadChartImage = (chartRef, fileName) => {
    if (chartRef.current) {
      const url = chartRef.current.toBase64Image();
      const link = document.createElement('a');
      link.href = url;
      link.download = `${dataType}_${fileName}.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  if (!dataType) {
    return (
      <Box sx={{ flexGrow: 1, p: 4, mt: 10, minHeight: 'calc(100vh - 64px)' }}>
        <Typography variant="h5" sx={{ textAlign: 'center', color: '#212121' }}>
          Please upload data to view the summary. Navigate to the{' '}
          <a href="/upload" style={{ color: '#FFD700', textDecoration: 'none' }}>Upload Data</a> page.
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ flexGrow: 1, p: 4, minHeight: 'calc(100vh - 64px)' }} className="dashboard-container">
      <style>
        {`
          @import url('https://fonts.googleapis.com/css2?family=SF+Pro+Display:wght@300;400;500;600;700&display=swap');

          @keyframes pulse {
            0% { transform: scale(1); }
            50% { transform: scale(1.03); }
            100% { transform: scale(1); }
          }

          @keyframes float {
            0% { transform: translateY(0) rotateY(30deg); }
            50% { transform: translateY(-10px) rotateY(30deg); }
            100% { transform: translateY(0) rotateY(30deg); }
          }

          @keyframes cardLift {
            0% { transform: translateZ(0); }
            50% { transform: translateZ(10px); }
            100% { transform: translateZ(0); }
          }

          .dashboard-container {
            position: relative;
            background: linear-gradient(135deg, rgba(0, 0, 0, 0.02) 0%, rgba(240, 240, 240, 0.02) 100%);
            overflow: hidden;
            font-family: 'SF Pro Display', -apple-system, sans-serif;
            perspective: 1000px;
          }

          .dashboard-container::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: url(${ethiopianMapUrl}) no-repeat center;
            background-size: cover;
            opacity: 0.08;
            filter: grayscale(100%) blur(5px) brightness(1.1);
            transform: perspective(1200px) rotateX(15deg) scale(1.1);
            z-index: -1;
          }

          .dashboard-container::after {
            content: '';
            position: absolute;
            top: 50px;
            right: 50px;
            width: 200px;
            height: 200px;
            background: url(${educationIconUrl}) no-repeat center;
            background-size: contain;
            opacity: 0.15;
            transform: perspective(800px) rotateY(45deg) rotateX(20deg) translateZ(20px);
            filter: drop-shadow(0 10px 20px rgba(0, 0, 0, 0.2));
            animation: float 6s ease-in-out infinite;
            z-index: -1;
          }

          .summary-card {
            transition: transform 0.4s ease, box-shadow 0.4s ease;
            background: rgba(255, 255, 255, 0.85);
            backdrop-filter: blur(15px);
            border-radius: 20px;
            transform: translateZ(0);
          }

          .summary-card:hover {
            transform: translateY(-8px) translateZ(15px);
            box-shadow: 0 20px 40px rgba(0, 0, 0, 0.12);
            animation: cardLift 1.5s infinite;
          }
        `}
      </style>
      <Typography
        variant="h3"
        gutterBottom
        sx={{
          textAlign: 'center',
          color: '#1D1D1F',
          fontWeight: 600,
          mb: 5,
          textTransform: 'none',
          letterSpacing: '0.5px',
          animation: 'pulse 2s infinite',
        }}
      >
        {dataType} Data Overview
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12}>
          <SummaryStats data={filteredData} dataType={dataType} />
        </Grid>

        {filteredData.length === 0 && (
          <Grid item xs={12}>
            <Typography variant="h6" sx={{ textAlign: 'center', color: '#1D1D1F', fontWeight: 400 }}>
              No data available to display. Please upload data via the{' '}
              <a href="/upload" style={{ color: '#007AFF', textDecoration: 'none' }}>Upload Data</a> page.
            </Typography>
          </Grid>
        )}

        {filteredData.length > 0 && (
          <>
            <Grid item xs={12} md={6}>
              <Fade in={filteredData.length > 0} timeout={800}>
                <Paper
                  elevation={6}
                  className="summary-card"
                  sx={{ p: 3, borderRadius: 20 }}
                >
                  <Typography
                    variant="h5"
                    gutterBottom
                    sx={{ color: '#1D1D1F', fontWeight: 500, mb: 2 }}
                  >
                    Overall Performance
                  </Typography>
                  <div style={{ height: '350px' }}>
                    <Radar
                      ref={chartRefs.radarChartRef}
                      data={getRadarChartData()}
                      options={{
                        responsive: true,
                        maintainAspectRatio: false,
                        scales: {
                          r: {
                            beginAtZero: true,
                            max: 100,
                            ticks: { stepSize: 25, color: '#1D1D1F' },
                            pointLabels: { color: '#1D1D1F', font: { size: 12 } },
                            grid: { color: 'rgba(0, 0, 0, 0.05)' },
                          },
                        },
                        plugins: {
                          legend: {
                            position: 'top',
                            labels: { color: '#1D1D1F', font: { size: 12 } },
                          },
                          tooltip: {
                            enabled: true,
                            backgroundColor: 'rgba(29, 29, 31, 0.9)',
                            titleColor: '#fff',
                            bodyColor: '#fff',
                          },
                        },
                      }}
                    />
                  </div>
                  <Typography
                    variant="body2"
                    sx={{ mt: 2, color: '#6E6E73', fontWeight: 400 }}
                  >
                    This chart shows the overall average score and success rate across all {dataType.toLowerCase()} data.
                  </Typography>
                </Paper>
              </Fade>
            </Grid>
          </>
        )}
      </Grid>
    </Box>
  );
};

export default Summary;
