import React, { useContext, useMemo } from 'react';
import { Box, Grid, Typography, Paper, Fade } from '@mui/material';
import { useTheme } from '@mui/material/styles';
import { Radar } from 'react-chartjs-2';
import { DataContext } from '../context/DataContext';
import SummaryStats from '../components/charts/SummaryStats';

const ethiopianMapUrl = 'https://upload.wikimedia.org/wikipedia/commons/thumb/7/71/Flag_of_Ethiopia.svg/1200px-Flag_of_Ethiopia.svg.png';
const educationIconUrl = 'https://png.pngtree.com/png-vector/20230417/ourmid/pngtree-3d-graduation-cap-icon-vector-png-image_6704102.png';

const Summary = () => {
  const { data, dataType } = useContext(DataContext);
  const theme = useTheme();
  const chartRefs = {
    radarChartRef: React.createRef(),
  };

  const filteredData = useMemo(() => {
    return data.filter((student) => student.sex === 'Male' || student.sex === 'Female');
  }, [data]);

  const getRadarChartData = () => {
    if (!filteredData || filteredData.length === 0) {
      return {
        labels: ['No Data'],
        datasets: [{
          label: 'Summary Metrics',
          data: [0, 0, 0],
          backgroundColor: 'rgba(204, 204, 204, 0.2)',
          borderColor: '#ccc',
          borderWidth: 2,
        }],
      };
    }

    if (dataType === 'HighSchool') {
      const classes = [...new Set(filteredData.map((student) => student.predictionclass).filter(cls => cls))];
      const metrics = classes.map((cls) => {
        const classData = filteredData.filter((student) => student.predictionclass === cls);
        const avgScore = classData.length > 0
          ? (classData.reduce((sum, student) => sum + Number(student.average), 0) / classData.length).toFixed(2)
          : 0;
        const passRate = classData.length > 0
          ? ((classData.filter(student => student.average >= 50).length / classData.length) * 100).toFixed(2)
          : 0;
        const avgAbsent = classData.length > 0
          ? (classData.reduce((sum, student) => sum + Number(student.absent || 0), 0) / classData.length).toFixed(2)
          : 0;
        return [avgScore, passRate, avgAbsent];
      });

      return {
        labels: ['Average Score', 'Success Rate (%)', 'Average Absenteeism (Days)'],
        datasets: classes.map((cls, index) => ({
          label: cls,
          data: metrics[index],
          backgroundColor: `rgba(${index === 0 ? '0, 128, 0' : index === 1 ? '255, 215, 0' : '255, 0, 0'}, 0.2)`,
          borderColor: index === 0 ? '#008000' : index === 1 ? '#FFD700' : '#FF0000',
          borderWidth: 2,
          pointBackgroundColor: index === 0 ? '#008000' : index === 1 ? '#FFD700' : '#FF0000',
          pointBorderColor: '#fff',
          pointHoverBackgroundColor: '#fff',
          pointHoverBorderColor: index === 0 ? '#008000' : index === 1 ? '#FFD700' : '#FF0000',
        })),
      };
    } else if (dataType === 'University') {
      const departments = [...new Set(filteredData.map((student) => student.dept).filter(dept => dept))];
      const metrics = departments.map((dept) => {
        const deptData = filteredData.filter((student) => student.dept === dept);
        const avgScore = deptData.length > 0
          ? (deptData.reduce((sum, student) => sum + Number(student.score), 0) / deptData.length).toFixed(2)
          : 0;
        const successRate = deptData.length > 0
          ? ((deptData.filter(student => ['excellent', 'good'].includes(student.status)).length / deptData.length) * 100).toFixed(2)
          : 0;
        const avgYear = deptData.length > 0
          ? (deptData.reduce((sum, student) => sum + Number(student.degreeawardeddate || 0), 0) / deptData.length).toFixed(2)
          : 0;
        return [avgScore, successRate, avgYear];
      });

      return {
        labels: ['Average Score', 'Success Rate (%)', 'Avg Degree Year'],
        datasets: departments.map((dept, index) => ({
          label: dept,
          data: metrics[index],
          backgroundColor: `rgba(${index === 0 ? '0, 128, 0' : index === 1 ? '255, 215, 0' : '255, 0, 0'}, 0.2)`,
          borderColor: index === 0 ? '#008000' : index === 1 ? '#FFD700' : '#FF0000',
          borderWidth: 2,
          pointBackgroundColor: index === 0 ? '#008000' : index === 1 ? '#FFD700' : '#FF0000',
          pointBorderColor: '#fff',
          pointHoverBackgroundColor: '#fff',
          pointHoverBorderColor: index === 0 ? '#008000' : index === 1 ? '#FFD700' : '#FF0000',
        })),
      };
    }
    return {
      labels: ['No Data'],
      datasets: [{
        label: 'Summary Metrics',
        data: [0, 0, 0],
        backgroundColor: 'rgba(204, 204, 204, 0.2)',
        borderColor: '#ccc',
        borderWidth: 2,
      }],
    };
  };

  const downloadCSV = () => {
    const csvContent = 'data:text/csv;charset=utf-8,' + filteredData.map((row) => Object.values(row).join(',')).join('\n');
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement('a');
    link.setAttribute('href', encodedUri);
    link.setAttribute('download', `${dataType}_summary_data.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const downloadJSON = () => {
    const jsonContent = JSON.stringify(filteredData, null, 2);
    const blob = new Blob([jsonContent], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `${dataType}_summary_data.json`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const downloadChartImage = (chartRef, fileName) => {
    if (chartRef.current) {
      const url = chartRef.current.toBase64Image();
      const link = document.createElement('a');
      link.href = url;
      link.download = `${dataType}_${fileName}.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  if (!dataType) {
    return (
      <Box sx={{ flexGrow: 1, p: 4, mt: 10, minHeight: 'calc(100vh - 64px)' }}>
        <Typography variant="h5" sx={{ textAlign: 'center', color: '#212121' }}>
          Please upload data to view the summary. Navigate to the{' '}
          <a href="/upload" style={{ color: '#FFD700', textDecoration: 'none' }}>Upload Data</a> page.
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ flexGrow: 1, p: 4, minHeight: 'calc(100vh - 64px)' }} className="dashboard-container">
      <style>
        {`
          @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;700&display=swap');

          @keyframes pulse {
            0% { transform: scale(1); }
            50% { transform: scale(1.05); }
            100% { transform: scale(1); }
          }

          @keyframes float {
            0% { transform: translateY(0) rotateY(30deg); }
            50% { transform: translateY(-15px) rotateY(30deg); }
            100% { transform: translateY(0) rotateY(30deg); }
          }

          @keyframes gradientShift {
            0% { background-position: 0% 50%; }
            50% { background-position: 100% 50%; }
            100% { background-position: 0% 50%; }
          }

          .dashboard-container {
            position: relative;
            background: linear-gradient(135deg, rgba(0, 128, 0, 0.1) 0%, rgba(255, 215, 0, 0.1) 50%, rgba(255, 0, 0, 0.1) 100%), url(${ethiopianMapUrl});
            background-size: 200% 200%, cover;
            background-position: center, center;
            background-repeat: no-repeat, no-repeat;
            background-attachment: fixed, fixed;
            animation: gradientShift 12s ease-in-out infinite;
            overflow: hidden;
            font-family: 'Poppins', sans-serif;
          }

          .dashboard-container::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: url(${ethiopianMapUrl}) no-repeat center;
            background-size: cover;
            opacity: 0.15;
            filter: grayscale(70%) brightness(1.2);
            transform: perspective(1200px) rotateX(15deg);
            z-index: -1;
          }

          .dashboard-container::after {
            content: '';
            position: absolute;
            top: 40px;
            right: 40px;
            width: 250px;
            height: 250px;
            background: url(${educationIconUrl}) no-repeat center;
            background-size: contain;
            opacity: 0.3;
            transform: perspective(1000px) rotateY(45deg) rotateX(25deg);
            filter: drop-shadow(0 15px 25px rgba(0, 0, 0, 0.4));
            animation: float 8s ease-in-out infinite;
            z-index: -1;
          }

          .summary-card {
            transition: transform 0.3s ease, box-shadow 0.3s ease;
          }

          .summary-card:hover {
            transform: translateY(-5px);
            box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
          }
        `}
      </style>
      <Typography
        variant="h3"
        gutterBottom
        sx={{
          textAlign: 'center',
          color: '#212121',
          fontWeight: 700,
          mb: 6,
          textTransform: 'uppercase',
          letterSpacing: '1px',
          animation: 'pulse 2s infinite',
        }}
      >
        {dataType} Data Overview
      </Typography>

      <Grid container spacing={4}>
        <Grid item xs={12}>
          <SummaryStats data={filteredData} dataType={dataType} />
        </Grid>

        {filteredData.length === 0 && (
          <Grid item xs={12}>
            <Typography variant="h6" sx={{ textAlign: 'center', color: '#212121', fontWeight: 500 }}>
              No data available to display. Please upload data via the{' '}
              <a href="/upload" style={{ color: '#FFD700', textDecoration: 'none' }}>Upload Data</a> page.
            </Typography>
          </Grid>
        )}

        {filteredData.length > 0 && (
          <>
            <Grid item xs={12} md={6}>
              <Fade in={filteredData.length > 0} timeout={1000}>
                <Paper
                  elevation={8}
                  className="summary-card"
                  sx={{
                    p: 3,
                    borderRadius: 16,
                    background: 'rgba(255, 255, 255, 0.98)',
                    backdropFilter: 'blur(10px)',
                  }}
                >
                  <Typography
                    variant="h5"
                    gutterBottom
                    sx={{ color: '#212121', fontWeight: 600, mb: 2 }}
                  >
                    Performance Summary Radar
                  </Typography>
                  <div style={{ height: '400px' }}>
                    <Radar
                      ref={chartRefs.radarChartRef}
                      data={getRadarChartData()}
                      options={{
                        responsive: true,
                        maintainAspectRatio: false,
                        scales: {
                          r: {
                            beginAtZero: true,
                            max: dataType === 'HighSchool' ? 100 : 100,
                            ticks: { stepSize: 20, color: '#212121' },
                            pointLabels: { color: '#212121', font: { size: 14 } },
                            grid: { color: 'rgba(0, 0, 0, 0.1)' },
                          },
                        },
                        plugins: {
                          legend: {
                            position: 'top',
                            labels: { color: '#212121', font: { size: 14 } },
                          },
                          tooltip: { enabled: true, backgroundColor: '#fff', titleColor: '#212121', bodyColor: '#212121' },
                        },
                      }}
                    />
                  </div>
                  <Typography
                    variant="body2"
                    sx={{ mt: 2, color: '#424242', fontWeight: 400 }}
                  >
                    {dataType === 'HighSchool'
                      ? 'This radar chart compares average scores, success rates, and absenteeism across performance categories.'
                      : 'This radar chart compares average scores, success rates, and average degree years across departments.'}
                  </Typography>
                </Paper>
              </Fade>
            </Grid>
          </>
        )}
      </Grid>
    </Box>
  );
};

export default Summary;
